import React, { useRef, useState } from 'react';
// import emailjs from 'emailjs-com';
import styles from './styles.css';
import emailjs from '@emailjs/browser';
// import axios from 'axios';

const ContactForm = () => {

    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs.sendForm('service_rb8duh9','template_dm4sw0p',form.current,'l0CpadP06y2rdMhxT')
        .then( (result) => {
            alert("Se ha enviado el mail de forma correcta.");
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
    }


    // const [ formData, setFormData ] = useState({
    //     name: '',
    //     phone: '',
    //     email: '',
    //     message: ''
    // });

    // const handleChange = (e) => {
    //     setFormData({ ...formData, [e.target.name]: e.target.value});
    // };

    // const handleSubmit = async (e) => {
    //     e.preventDefault();
    //     try {
    //         //PROBAR CON HTTPS acá abajo .
    //         await axios.post('http://localhost:3001/send-email', formData);
    //         alert('Correo electrónico enviado correctamente');
    //         //Acción adicional de limpiar el formulario luego de enviar el correo
    //         setFormData({
    //             name: '',
    //             phone: '',
    //             email: '',
    //             message: ''
    //         });
    //     } catch (error) {
    //         console.error(error);
    //         alert('Error al enviar el correo electrónico');
    //     }
    // };

    return (
        // <div className='formContainer'>
            <div className="login-box" id='Contacto'>
                <h2>CONTACTO</h2>
                <form ref={form} onSubmit={sendEmail}>
                    <div className=''>
                        <div className="user-box">
                        <input 
                            type="text" 
                            name="user_name" 
                            // value={formData.name}
                            // onChange={handleChange}
                            required/>
                        <label>Nombre</label>
                        </div>
                        <div className="user-box">
                        <input
                            type="number" 
                            name="user_phone"
                            // value={formData.phone}
                            // onChange={handleChange}
                            required/>
                        <label>Teléfono</label>
                        </div>
                    </div>
                    <div className="user-box">
                    <input 
                        type="mail" 
                        name="user_email"
                        // value={formData.email}
                        // onChange={handleChange}
                        required/>
                    <label>Email</label>
                    </div>
                    <div className="user-box">
                        {/* <textarea name="message" cols="60" rows="5"/> */}
                    <input 
                        type="text" 
                        name="message"
                        // value={formData.message}
                        // onChange={handleChange}
                        required/>
                    <label>Deje su consulta</label>
                    </div>
                    <button id='button-submit' type='submit'>
                    
                    <span></span>
                    <span></span>
                    <span></span>
                    <span></span>
                        Enviar
                    </button>
                </form>
            </div>
        // </div>
    )
}

export default ContactForm;
